                        <?php

                        function printClassified($newsArray, $newsCategory) {

                            for ($x = 0; $x <= count($newsArray); $x = $x + 4) {
                                echo '<tr>';
                                if ($x >= count($newsArray)) {
                                    break;
                                }
                                for ($m = 0; $m <= 3; $m++) {
                                    if ($x + $m >= count($newsArray)) {
                                        break;
                                    }
                                    echo '<td><div class="input-group-btn"><textarea rows="5" cols="50">' . trim(preg_replace("/\r|\r\n|\n/", "", html_entity_decode($newsArray[$x + $m]))) . '</textarea></div></td>';
                                }
                                echo '</tr>';
                            }

                            echo '<tr><td><td colspan="3"></td><td><button type="button" class="btn-lg btn-warning">' . $newsCategory . '<span class="badge">' . count($newsArray) . '</span></button></td></tr>';
                            echo "<tr><td>&nbsp;</td></tr>";
                            echo "<tr><td>&nbsp;</td></tr>";
                        }

                        printClassified($politics, "Politics ");
                        printClassified($technology, "Technology ");
                        printClassified($sports, "Sports ");
                        printClassified($business, "Business ");
                        printClassified($entertainment, "Entertainment ");
                        ?>
                    </table>
